import React from 'react';
import emailjs from 'emailjs-com';
import '../../App.css'

// import './ContactUs.css';

export default function ContactUs() {

  function sendEmail(e) {
    e.preventDefault();

    emailjs.sendForm('service_iw9cpkg', 'template_uklwa1d', e.target, 'user_RoT5XVDeZjbLf8pUvtPnS')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
  }

  return (
      <div className="Contact-container">
    <form className="contact-form" onSubmit={sendEmail}>
      <input  className="inputstyle" style ={{width: '400px'}} type="hidden" name="contact_number" />
      <label >Name</label>
      <input  className="inputstyle" style ={{width: '400px'}} type="text" name="user_name" />
      <label>Email</label>
      <input  className="inputstyle" style ={{width: '400px'}} type="email" name="user_email" />
      <label>Message</label>
      <textarea name="message" style ={{width: '400px'}} rows='4'/>
      <input  className="inputstyle" style ={{width: '400px'}} type="submit" value="Send" />
    </form>
    </div>
  );
}
