import React from 'react';
import Navbar from './components/Navbar';
import './App.css';
import Home from './components/pages/Home';
import { BrowserRouter as Router, Switch, Route, state } from 'react-router-dom';
import Services from './components/pages/Services';
import Time_Table from './components/pages/Time-Table';
import SignUp from './components/pages/SignUp';
import ContactUs from './components/pages/ContactUs';
import UploadFile from './components/UploadForm';
import UploadForm from './components/UploadForm';
import axios from 'axios'
// import Mailer from './components/mailer';


// class App extends Component{
//   render() {
//     return (
//       <div className ='App'>
//         <input type ="file" />
//       </div>
//     )
//   }
// }
function App() {

  state = {
    seletedFile: null
  }
  // fileSelectedHandler = event => {
  //   this.setState({
  //     seletedFile: event.target.files[0]

  //   })
  // }
    // console.log(event.target.files[-0]);
   
  fileUploadHnadler = () =>{
    const formData = new FormData();
    formData.append('image', this.state.seletedFile,  this.state.seletedFile.name);
    axios.post('gs://myapp-739e0.appspot.com', formData)
    .then(response=>{
      console.log(response)
    })
    

  }
  return (
      <Router input type='file' onChange={this.fileSelectedHandler}  >
        <button onClick={this.fileSelectedHandler}>Upload</button>
      
        <Navbar />
        <Switch>

          <Route path='/' exact component={Home}  />
          <Route path='/services' component={Services} />
          <Route path='/tame-table' component={Time_Table} />
          <Route exact path='/contact' component={ContactUs} />
          <Route exact path='/contact' component={ContactUs} />
          < UploadForm />

           {/* <Route path ='/Mailer' component={Mailer} />  */}
        </Switch>
      </Router>
  
  );
}

export default App;
