import React from 'react';
import '../App.css';
import { Button } from './Button';
import './HeroSection.css';

function HeroSection() {
  return (
    <div className='hero-container'>
      {/* <video src='/videos/video-1.mp4' autoPlay loop muted /> */}
      <h1>Welcome To Limitless Page
        <p>

חלל אינטימי לאימונים, סדנאות והרצאות 
אימונים שיקומים, קבוצות קטנות ואימונים אישים
בחיבור עם הקשבה לגוף ולנפש
כתובתינו - אבולעפיה 22 תל אביב
        </p>
      </h1>
      <p>Find Your Trainer Here</p>
      <div className='hero-btns'>
        <Button
          className='btns'
          buttonStyle='btn--outline'
          buttonSize='btn--large'
        >
          Join limitless
        </Button>
        {/* <Button
          className='btns'
          buttonStyle='btn--primary'
          buttonSize='btn--large'
          onClick={console.log('')}
        >
          Professional trainer <i className='far fa-play-circle' />
        </Button> */}
      </div>
    </div>
  );
}

export default HeroSection;
