 import React from 'react';
import emailjs from 'emailjs-com';
// import '../../App.css'



const ContactUs = () => {
  
  function sendEmail(e){
    e.preventDefault();
    //before this step you should have emailjs account first
    emailjs.sendForm('service_iw9cpkg', 'template_uklwa1d', e.target, 'user_RoT5XVDeZjbLf8pUvtPnS')
    .then(result =>{
      console.log(result.text);

    },
    
    
    
    
    (error) => {
      console.log(error.text)
    });

  }
    return(
        <div className = "container border" style={{ marginTop:"50px", width:"50%", backgoundImage: `url('/images/courage2.jpg') `, backgroundPosition: 'center', backgroundSize: 'cover',}} > 
        
            <h1 style={{marginTop: '25p'}}>Contact Form/טופס יצירת קשר</h1>

            <form  className="row" style={{ margin: "25px 85px 75px 100px"}} onSubmit={sendEmail}>

                <label>Name/שֵׁם</label>

                <input type ="text" name="name" className="form-control"/>

                <label>Email/אימייל</label>

                <input type ="email" name="user_name"  className="form-control"/>

                <label>message/הוֹדָעָה</label>

                <textarea name="message" rows='4' className="form-control" />

                <input type='submit' value='Send/לִשְׁלוֹחַ' className="form-control btn btn-secondary" style={{marginTop: "20px"}} />

            </form>

            <div className ="bussinessInfo" style={{backgroundColor:'white', textDecorationColor:"green" }}>
                <h3 style={{textAlign: 'center', backgroundColor:'blue'}}>Business Address/כתובת עסקית
                <br /><p>kkkkk</p></h3>

                <label>Phone Number: 0538248164</label>
                <hr />
                <label>Email : limitlesselin@gmail.com</label>
                <hr />
                <label>Address: Abulafia 22 Tel Aviv</label>
                </div>

          
        </div>
        )
        
}
export default ContactUs ;


